<?php/** * Read Database *  * Ajax functions *  * @package WordPress * @subpackage read database * @author Rahul Naik <naikrahulda@gmail.com> */if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directlyfunction rd_init() {            if(current_user_can('administrator')) {        // Initializing read_DB class        new Read_DB();           }    }function rd_ajax() {     check_ajax_referer( 'rdSecure', 'secure' );    if(current_user_can('administrator')) {        // Initializing read_DB          $rd = new Read_DB();        // Send values as AJAX response         echo $rd->read_db_ajax(sanitize_text_field($_POST['table_name']));      die;    } else        return;    }